import artists from"./json/artists.json";Vue.component("tabs",{template:'\n        <div>\n            <div class="nav">\n              <ul>\n                <li v-for="tab in tabs" :class="[\'navBtn\', { \'is-active\': tab.isActive, \'homeBtn\': tab.home }]">\n                    <a :href="tab.href" @click="selectTab(tab)">{{ tab.name }}</a>\n                </li>\n                <li class="navBtn mainBtn">\n                    <a target="_blank" href="https://hubs.mozilla.com/t7ZhGX6/the-trouble-with-double-vr">\n                        <span class="glitch-block" data-text="visit">vr exhibition</span>\n                    </a>\n                </li>\n              </ul>\n            </div>\n\n            <div>\n                <slot></slot>\n            </div>\n        </div>\n    ',data:()=>({tabs:[]}),created(){this.tabs=this.$children},methods:{selectTab(t){this.tabs.forEach(e=>{e.isActive=e.name==t.name})}}}),Vue.component("tab",{template:'\n        <div v-show="isActive"><slot></slot></div>\n    ',props:{name:{required:!0},selected:{default:!1},home:{default:!1}},data:()=>({isActive:!1}),computed:{href(){return this.home?"#":"#"+this.name.toLowerCase().replace(/ /g,"-")}},mounted(){this.isActive=this.selected}}),new Vue({el:"#app",data:{artists:artists}});